import './App.css';
import Coffee from "./Coffee";
import {useState} from "react";

function getRandomImage() {
  const coffeeArray = ['CoffeeBookGlasses', 'CoffeeBookWindows', 'Cappccino', 'CoffeeMug'];
  return coffeeArray[Math.floor(Math.random()*coffeeArray.length)];
}

function App() {
  const[coffees, setCoffees] = useState([]);

  const handleClick = () => {
    setCoffees([...coffees, getRandomImage()]);
  }

  const coffeeList = coffees.map((c, index) => {
    return <Coffee key={index} coffeeName={c} />
  })

  return (
    <div className="App">
      <button onClick={handleClick}>Gorüntü Ekle</button>
      <div>
        {coffeeList}
      </div>
    </div>
  );
}

export default App;
