import './App.css';
import Coffee from "./Coffee";
import {useState} from "react";

function getRandomImage() {
  const coffeeArray = ['CoffeeBookGlasses', 'CoffeeBookWindows', 'Cappccino', 'CoffeeMug'];
  return coffeeArray[Math.floor(Math.random()*coffeeArray.length)];
}

function App() {
  const[coffee, setCoffee] = useState();
  const handleClick = () => {
    setCoffee([...coffee, getRandomImage()]);
  }
  return (
    <div className="App">
      <button onClick={handleClick}>Gorüntü Ekle</button>
      {
        coffee.map
      }
    </div>
  );
}

export default App;
